  `id` int(11) NOT NULL AUTO_INCREMENT,
  `tit` varchar(255) COLLATE utf8mb4_vietnamese_ci NOT NULL,
  `des` varchar(500) COLLATE utf8mb4_vietnamese_ci DEFAULT NULL,
