INSERT INTO `services` VALUES (4, 'Dịch Vụ Báo Cáo Giao Dịch Liên Kết - Dịch vụ Báo Cáo Chuyển giá ', ' Lập Báo Cáo Giao Dịch Liên Kết  - Lập Báo Cáo Chuyển Giá - Transfer Pricing Reports', '<h2><span style=\"color:windowtext;\"><strong>DỊCH VỤ CHUYỂN GIÁ</strong></span></h2><h3>&nbsp;</h3><h3>Dịch vụ chuyển giá</h3><h3>Chuyển giá/ Chuyen gia&nbsp;</h3><h3>Các giao dịch liên kết</h3><h3>Giao dịch liên kết/ Giao dich lien ket &nbsp;</h3><h3>Khái niệm chuyển giá&nbsp;</h3><h3>Giao dịch liên kết là gì&nbsp;</h3><h3>Doanh nghiệp có giao dịch liên kết là gì</h3><h3>Xác định giao dịch liên kết &nbsp;&nbsp;</h3><h3>Doanh nghiệp có giao dịch liên kết</h3><h3>Giao dịch liên kết tiếng anh là gì&nbsp;</h3><h3>Chuyển giá tiếng anh là gì</h3><h3>Chuyển giá trốn thuế là gì/ Chuyển giá trốn thuế &nbsp;&nbsp;</h3><h3>Chuyển giá trong doanh nghiệp fdi&nbsp;</h3><h3>Các hình thức giao dịch liên kết&nbsp;</h3><h3>Quy định về giao dịch liên kết&nbsp;</h3><h3>Quy định về chuyển giá&nbsp;</h3><h3>Công ty có giao dịch liên kết&nbsp;</h3><h3>Hình thức giao dịch liên kết&nbsp;</h3><h3>Giao dịch liên kết đặc thù là gì&nbsp;</h3><h3>Giao dịch liên kết và chuyển giá&nbsp;</h3><h3>Miễn lập hồ sơ giao dịch liên kết</h3><h3>Miễn kê khai giao dịch liên kết</h3><h3>Ví dụ về giao dịch liên kết</h3><h3>Chuyển giá là gì ví dụ/ Chuyển giá là gì/ Ví dụ về chuyển giá&nbsp;</h3><p>Định nghĩa về giá chuyển nhượng</p><p>Giá chuyển nhượng là giá mua hoặc bán hàng hoá và dịch vụ được thoả thuận giữa các Bên có liên quan, phải ở giá trị như khi chúng được mua hoặc bán cho các công ty không có liên quan.</p><p>Mục tiêu của Định giá chuyển nhượng dựa trên nguyên tắc cạnh tranh tự do còn được gọi là “Chiều dài cánh tay” và mục đích là các Bên liên quan đánh thuế việc nộp thuế thu nhập tại quốc gia tạo ra lợi nhuận đó và tránh chuyển lợi nhuận sang những quốc gia có chế độ đánh thuế thấp hơn hoặc không đánh thuế và điều này đạt được bằng cách tránh sự thao túng giá cả.</p><h3>Các hình thức chuyển giá &nbsp;</h3><h3>Hoạt động chuyển giá/ Hiện tượng chuyển giá/ Chuyển giá tại Việt Nam/ chuyển giá ở Việt Nam&nbsp;</h3><h3>&nbsp;</h3><p>&nbsp;</p><h3>Các phương pháp xác định giá giao dịch liên kết&nbsp;</h3><h3>Phương pháp xác định giá giao dịch liên kết&nbsp;</h3><h3>Xác định giá giao dịch liên kết&nbsp;</h3><h3>Giao dịch liên kết khi quyết toán thuế tndn&nbsp;</h3><h3>&nbsp;</h3><h3>&nbsp;</h3><h3>Hồ sơ chuyển giá&nbsp;</h3><h3>Báo cáo chuyển giá&nbsp;</h3><h3>Hồ sơ giao dịch liên kết&nbsp;</h3><h3>Lập hồ sơ giao dịch liên kết&nbsp;</h3><h3>Cách làm giao dịch liên kết&nbsp;</h3><h3>Cách lập báo cáo giao dịch liên kết</h3><h3>Báo cáo giao dịch liên kết &nbsp;&nbsp;</h3><h3>Hướng dẫn lập tờ khai giao dịch liên kết&nbsp;</h3><h3>Hướng dẫn kê khai phụ lục giao dịch liên kết&nbsp;</h3><h3>Hướng dẫn kê khai mẫu 01 giao dịch liên kết&nbsp;</h3><h3>Phụ lục giao dịch liên kết&nbsp;</h3><h3>Phụ lục 01 giao dịch liên kết&nbsp;</h3><h3>Kê khai giao dịch liên kết&nbsp;</h3><h3>Hướng dẫn kê khai giao dịch liên kết&nbsp;</h3><h3>Kê khai giao dịch liên kết trên htkk</h3><h3>Mẫu 01 giao dịch liên kết&nbsp;</h3><h3>Tờ khai giao dịch liên kết</h3><h3>&nbsp;</h3><h3>Chi phí lãi vay đối với giao dịch liên kết&nbsp;</h3><h3>Chi phí lãi vay trong giao dịch liên kết&nbsp;</h3><h3>Chi phí lãi vay giao dịch liên kết&nbsp;</h3><h3>Công văn chi phí lãi vay giao dịch liên kết&nbsp;</h3><h3>Lãi vay trong giao dịch liên kết&nbsp;</h3><h3>Giao dịch liên kết lãi vay&nbsp;</h3><h3>Lãi vay giao dịch liên kết&nbsp;</h3><h3>Công văn giao dịch liên kết&nbsp;</h3><h3>Công văn tổng cục thuế giao dịch liên kết&nbsp;</h3><h3>&nbsp;</h3><h3>Thời hạn nộp báo cáo giao dịch liên kết&nbsp;</h3><h3>&nbsp;</h3><h3>Thông tư giao dịch liên kết&nbsp;</h3><h3>Quy định mới về giao dịch liên kết&nbsp;</h3><h3>Giao dịch liên kết theo nghị định 20&nbsp;</h3><h3>Nghị định 20 về giao dịch liên kết&nbsp;</h3><h3>Nghị định 132 về giao dịch liên kết&nbsp;</h3><h3>Thông tư về giao dịch liên kết&nbsp;</h3><h3>Thông tư 41 về giao dịch liên kết&nbsp;</h3><h3>Thông tư 41 giao dịch liên kết&nbsp;</h3><h3>Thông tư 66 về giao dịch liên kết&nbsp;</h3><h3>Thông tư 20 về giao dịch liên kết&nbsp;</h3><h3>Nghị định 20 giao dịch liên kết&nbsp;</h3><h3>Giao dịch liên kết nghị định 20&nbsp;</h3><h3>Nghi dinh 20 giao dich lien ket&nbsp;</h3><h3>Thông tư 20 giao dịch liên kết&nbsp;</h3><h3>Nghị định 20 về chuyển giá&nbsp;</h3><h3>Giao dịch liên kết 2020&nbsp;</h3><h3>Nghị định 20 chuyển giá&nbsp;</h3><h3>&nbsp;</h3><h3>&nbsp;</h3><p>&nbsp;</p><p>&nbsp;</p><h3><span style=\"color:rgb(255,192,0);\"><i><strong>Dịch vụ chuyển giá &nbsp;của chúng tôi làm giảmLo lắng, khó khăn, kết quả kém và rủi ro&nbsp;về nghĩa vụ thuế cho Quý Khách hàng</strong></i></span></h3><ul><li style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\"><i><strong>Đơn vị của Quý Khách hàng có phải là&nbsp;các bên có quan hệ liên kết thuộc một trong 10 trường hợp tại điều 5 Nghị định 20/2017/NĐ-CP ngày&nbsp;04/2/2017 của Chính phủ?!&nbsp;</strong></i>Bao gồm các loại hình doanh nghiệp sau đây:</span></li></ul><p style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\"><strong>Quan hệ về sở hữu vốn (4 trường hợp):</strong></span></p><p style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\">A. &nbsp; &nbsp; &nbsp; Một doanh nghiệp nắm giữ trực tiếp hoặc gián tiếp ít nhất 25% vốn góp của chủ sở hữu của doanh nghiệp kia;</span></p><p style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\">B. &nbsp; &nbsp; &nbsp; Cả hai doanh nghiệp đều có ít nhất 25% vốn góp của chủ sở hữu do một bên thứ ba nắm giữ trực tiếp hoặc gián tiếp;</span></p><p style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\">C. &nbsp; &nbsp; &nbsp; Một doanh nghiệp là cổ đông lớn nhất về vốn góp của chủ sở hữu của doanh nghiệp kia, nắm giữ trực tiếp hoặc gián tiếp ít nhất 10% tổng số cổ phần của doanh nghiệp kia;</span></p><p style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\">D. &nbsp; &nbsp; &nbsp;Một doanh nghiệp bảo lãnh hoặc cho một doanh nghiệp khác vay vốn dưới bất kỳ hình thức nào (bao gồm cả các khoản vay từ bên thứ ba được đảm bảo từ nguồn tài chính của bên liên kết và các giao dịch tài chính có bản chất tương tự) với điều kiện khoản vốn vay ít nhất bằng 25% vốn góp của chủ sở hữu của doanh nghiệp đi vay và chiếm trên 50% tổng giá trị các khoản nợ trung và dài hạn của doanh nghiệp đi vay;</span></p><p style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\"><strong>Quan hệ về quản lý, điều hành (6 trường hợp):</strong></span></p><p style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\">E. &nbsp; &nbsp; &nbsp;Một doanh nghiệp chỉ định thành viên ban lãnh đạo điều hành hoặc nắm quyền kiểm soát của một doanh nghiệp khác với điều kiện số lượng các thành viên được doanh nghiệp thứ nhất chỉ định chiếm trên 50% tổng số thành viên ban lãnh đạo điều hành hoặc nắm quyền kiểm soát của doanh nghiệp thứ hai; hoặc một thành viên được doanh nghiệp thứ nhất chỉ định có quyền quyết định các chính sách tài chính hoặc hoạt động kinh doanh của doanh nghiệp thứ hai;</span></p><p style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\">F. &nbsp; &nbsp; &nbsp;Hai doanh nghiệp cùng có trên 50% thành viên ban lãnh đạo hoặc cùng có một thành viên ban lãnh đạo có quyền quyết định các chính sách tài chính hoặc hoạt động kinh doanh được chỉ định bởi một bên thứ ba;</span></p><p style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\">G. &nbsp; &nbsp; Hai doanh nghiệp được điều hành hoặc chịu sự kiểm soát về nhân sự, tài chính và hoạt động kinh doanh bởi các cá nhân thuộc một trong các mối quan hệ vợ, chồng, cha đẻ, cha nuôi, mẹ đẻ, mẹ nuôi, con đẻ, con nuôi, anh ruột, chị ruột, em ruột, anh rể, em rể, chị dâu, em dâu, ông nội, bà nội, cháu nội, ông ngoại, bà ngoại, cháu ngoại, cô, chú, bác, cậu, dì ruột và cháu ruột;</span></p><p style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\">H. &nbsp; &nbsp; &nbsp;Hai cơ sở kinh doanh có mối quan hệ trụ sở chính và cơ sở thường trú hoặc cùng là cơ sở thường trú của tổ chức, cá nhân nước ngoài;</span></p><p style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\">I. &nbsp; &nbsp; &nbsp; &nbsp;Một hoặc nhiều doanh nghiệp chịu sự kiểm soát của một cá nhân thông qua vốn góp của cá nhân này vào doanh nghiệp đó hoặc trực tiếp tham gia điều hành doanh nghiệp;</span></p><p style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\">K. &nbsp; &nbsp; &nbsp;Các trường hợp khác trong đó doanh nghiệp chịu sự điều hành, kiểm soát quyết định trên thực tế đối với hoạt động sản xuất kinh doanh của doanh nghiệp kia.</span></p><p style=\"text-align:justify;\">&nbsp;</p><ul><li style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\"><i><strong>Quý Doanh nghiệp gặp khó khăn, rủi ro thế nào nếu không tuân thủ quy định về chuyển giá?</strong></i></span></li></ul><p style=\"text-align:justify;\">&nbsp;</p><ol style=\"list-style-type:upper-alpha;\"><li style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\"><i><strong>Rủi ro, khó khăn về vấn đề tuân thủ:</strong></i> Không đáp ứng kịp thời các yêu cầu tuân thủ mới quy định tại Nghị định 20 và Thông tư 41, bao gồm việc lập và nộp 04 mẫu tờ khai mới, việc chuẩn bị Hồ sơ quốc gia (Local File) và Hồ sơ toàn cầu (Master File).</span></li></ol><p style=\"text-align:justify;\">&nbsp;</p><ol style=\"list-style-type:upper-alpha;\"><li style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\"><i><strong>Không thể giải trình với cơ quan Thuế:</strong></i> Không biết cách chuẩn bị giải trình các khác biệt trọng yếu giữa Hồ sơ quốc gia (Local File), Hồ sơ toàn cầu (Master File) và thông tin trên Báo cáo lợi nhuận liên quốc gia (Country-by-Country Report).</span></li></ol><p style=\"text-align:justify;\">&nbsp;</p><ol style=\"list-style-type:upper-alpha;\"><li style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\"><i><strong>Hiểu sai, trình bày sai quy định:</strong></i> Không cập nhật kịp thời các thay đổi giữa quy định cũ và quy định mới dẫn đến hiểu sai và trình bày không phù hợp, không nhất quán các thông tin trên các mẫu biểu mới, hồ sơ giá chuyển nhượng và các thông tin cần cung cấp cho Cơ quan thuế.</span></li></ol><p style=\"text-align:justify;\">&nbsp;</p><ul><li style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\">Theo quy định mới từ Nghị định 20 và Thông tư 41 thì Cơ quan thuế được quyền ấn định thuế trong nhiều trường hợp nếu Quý Doanh nghiệp không tuân thủ quy định về chuyển giá.</span></li></ul><p style=\"text-align:justify;\">&nbsp;</p><ul><li style=\"text-align:justify;\">Nếu việc ấn định thuế xảy ra thì Quý Doanh nghiệp sẽ bị truy thu thuế, truy thu lãi chậm nộp thuế, phạt thuế do khai sai dẫn đến truy thu. Trong trường hợp bị xem là cố tình vi phạm, Quý Doanh nghiệp có thể bị phạt thêm từ 1 đến 3 lần số thuế bị truy thu.</li></ul><p>&nbsp;</p><ul><li>Do việc thanh tra về chuyển giá thường bao gồm nhiều kỳ&nbsp;thuế (nhiều năm tính thuế) nên nếu việc ấn định thuế xảy ra thì khoản truy thu, thu lãi chậm nộp, phạt thuế lũy kế có thể rất lớn, ảnh hưởng đến tình hình tài chính của Quý Doanh nghiệp. Việc bị ấn định thuế có thể còn làm thay đổi kết quả chuyển lỗ, kết quả tính&nbsp;toán&nbsp;ưu&nbsp;đãi&nbsp;thuế&nbsp;và&nbsp;ảnh&nbsp;hưởng&nbsp;tình&nbsp;trạng&nbsp;thuế,&nbsp;kế&nbsp;hoạch&nbsp;thuế,&nbsp;kế&nbsp;hoạch&nbsp;tàichính&nbsp;của Quý Doanh nghiệp ở những năm tiếp&nbsp;theo.</li></ul><p style=\"text-align:justify;\">&nbsp;</p><ul><li style=\"text-align:justify;\"><a href=\"http://www.rsm.com.vn/dich-vu-chuyen-gia\"><span style=\"color:rgb(65,65,65);\"><i><strong><u>Tại sao quý doanh nghiệp cần thực hiện dịch vụ chuyển giá?</u></strong></i></span></a></li></ul><p style=\"text-align:justify;\">Cùng với việc các công ty đa quốc gia tìm cách cạnh tranh hiệu quả trong thị trường toàn cầu, các giao dịch xuyên quốc gia giữa các công ty liên quan ngày càng tăng về cả số lượng và độ phức tạp.</p><p style=\"text-align:justify;\">Trước&nbsp;xu&nbsp;hướng&nbsp;này,&nbsp;các&nbsp;cơ&nbsp;quan&nbsp;thuế&nbsp;trên&nbsp;khắp&nbsp;thế&nbsp;giới&nbsp;đang&nbsp;ngày&nbsp;càng&nbsp;trở&nbsp;nên&nbsp;nhạy&nbsp;cảm&nbsp;với&nbsp;các&nbsp;cách thức mà hoạt động chuyển giá ảnh hưởng đến số thu thuế trong phạm vi từng quốc gia. Nhiều quốc gia và&nbsp;vùng&nbsp;lãnhthổ&nbsp;đang&nbsp;ứng&nbsp;phó&nbsp;bằng&nbsp;cách&nbsp;tăng&nbsp;cường&nbsp;hệ&nbsp;thống&nbsp;luật&nbsp;pháp,&nbsp;triển&nbsp;khai&nbsp;các&nbsp;yêu&nbsp;cầu&nbsp;nghiêm ngặt hơn về hồ sơ thuế, và áp dụng các hình thức phạt đối với vi&nbsp;phạm.</p><p style=\"text-align:justify;\">Tại Việt Nam, kể từ khi ban hành các quy định mới về xác định giá thị trường theo Nghị định 20/2017/NĐ-CP và&nbsp;Thông tư&nbsp;41/2017/TT-BTC,&nbsp;cơ&nbsp;quan&nbsp;Thuế&nbsp;các&nbsp;địa&nbsp;phương&nbsp;đã&nbsp;thực&nbsp;hiện&nbsp;nhiều&nbsp;hoạt&nbsp;động&nbsp;trong&nbsp;đó&nbsp;yêu&nbsp;cầu&nbsp;Quý Doanh nghiệp&nbsp;kê&nbsp;khai bổ sung thông tin về giao dịch liên kết và lập hồ sơ giải trình giao dịch theo giá thị trường theo quy&nbsp;định.</p><p style=\"text-align:justify;\">Gần đây nhất, Tổng Cục Thuế đang lên kế hoạch yêu cầu các cơ quan&nbsp;Thuế địa phương thực hiện hoạt động thanh tra thuế đối với các Quý Doanh nghiệp có vốn đầu tư nước ngoài có dấu hiệu chuyển giá.</p><p style=\"text-align:justify;\">Các dấu hiệu chuyển giá tập trung vào các vấn đề sau:</p><ul><li style=\"text-align:justify;\">Lợi nhuận biên thấp qua các&nbsp;năm</li><li style=\"text-align:justify;\">Lỗ liên tục qua các&nbsp;năm</li><li style=\"text-align:justify;\">Giá giao dịch biến động qua các&nbsp;năm</li><li style=\"text-align:justify;\">Lợi nhuận biên thay đổi bất thường qua các&nbsp;năm</li><li style=\"text-align:justify;\">Lỗ trong một vài năm hoặc trong một vài bộ&nbsp;phận</li><li style=\"text-align:justify;\">Các thỏa thuận chia sẻ chi&nbsp;phí</li><li style=\"text-align:justify;\">Các vướng mắc về Cơ sở thường&nbsp;trú</li><li style=\"text-align:justify;\">Quản lý năng lực sản&nbsp;xuất</li><li style=\"text-align:justify;\">Thanh toán phí bản quyền/ phí quản&nbsp;lý</li></ul><p>&nbsp;</p><p style=\"text-align:justify;\">Động&nbsp;thái&nbsp;gần&nbsp;đây&nbsp;của&nbsp;Tổng&nbsp;Cục&nbsp;Thuế&nbsp;đòi&nbsp;hỏi&nbsp;Quý&nbsp;Quý Doanh nghiệp&nbsp;cần&nbsp;chuẩn&nbsp;bị&nbsp;và&nbsp;duy&nbsp;trì&nbsp;tờ&nbsp;khai&nbsp;thông&nbsp;tin về giao dịch liên kết và hồ sơ giải trình giao dịch theo giá thị&nbsp;trường.</p><p style=\"text-align:justify;\">&nbsp;</p><figure class=\"image\"><img src=\"data:image/png;base64,\" alt=\"Smiling Face with Solid Fill\"></figure><h2><span style=\"color:rgb(255,192,0);\"><i><strong>&nbsp; &nbsp;Lợi ích, kết quả và mong muốn</strong></i></span><span style=\"color:windowtext;\"><i><strong>&nbsp;</strong>của Quý Khách hàng<strong>:</strong></i></span></h2><p style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\">&nbsp;</span></p><ul><li style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\">Những nội dung mới quy định trong&nbsp;Nghị định 20/2018/NĐ-CP và&nbsp;Thông tư 41/2018/TT-BTC đang khiến rất nhiều Quý Doanh nghiệp gặp khó khăn, bỡ ngỡ trong quá trình triển khai, áp dụng thực tế tại Quý Doanh nghiệp.</span></li></ul><p style=\"text-align:justify;\">&nbsp;</p><ul><li style=\"text-align:justify;\"><a href=\"http://www.rsm.com.vn/dich-vu-chuyen-gia\"><span style=\"color:rgb(65,65,65);\"><u>Dịch vụ chuyển giá của MAN&nbsp;</u></span></a><span style=\"color:rgb(65,65,65);\">sẽ giúp Quý Doanh nghiệp không chỉ quản trị tốt rủi ro về chuyển giá mà còn thực hiện đúng việc tuân thủ chính sách pháp luật và thiết kế các chính sách hiệu quả về quản lý chuyển giá.</span></li></ul><p style=\"text-align:justify;\">&nbsp;</p><ul><li style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\">Chúng tôi hỗ trợ khách hàng chuẩn bị hoặc rà soát tờ khai thông tin về giao dịch liên kết hàng năm nhằm đảm bảo thông tin được kê khai đầy đủ và nhất quán và hỗ trợ chuẩn bị báo cáo về chính sách giá. Chúng tôi cũng hỗ trợ đánh giá toàn bộ thực trạng chuyển giá của khách hàng và đưa ra các khuyến nghị mang tính chất thực tiễn để giảm thiểu rủi ro về chuyển giá và xây dựng chiến lược định giá để hỗ trợ cho việc thay đổi hoặc chuyển đổi hoạt động kinh doanh của doanh nghiệp. Trong trường hợp có tranh chấp với cơ quan thuế hoặc thanh tra, kiểm tra thuế, chúng tôi có thể hỗ trợ đàm phán với cơ quan có thẩm quyền, hỗ trợ giải quyết tranh chấp và tư vấn các giải pháp hữu hiệu nhằm rút ngắn thời gian thanh tra, kiểm tra thuế.&nbsp;</span></li></ul><p style=\"text-align:justify;\">&nbsp;</p><ul><li style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\">Đội ngũ chuyên gia thuế, kế toán viên và nhà kinh tế của chúng tôi đã làm việc với nhiều khách hàng để cung cấp các giải pháp chuyển giá phù hợp đã mang lại lợi ích thực sự cho Quý Doanh nghiệp của họ. Chúng tôi có nhiều năm kinh nghiệm làm việc trong \"Big 4\", và cũng trong nhóm chuyên gia chuyển giá tại HM Revenue and Customs. Điều này cung cấp cho chúng tôi những hiểu biết tuyệt vời về các giải pháp đã chứng tỏ thành công với khách hàng trên nhiều ngành khác nhau cũng như các chính sách và thủ tục được cơ quan thuế thông qua. Khả năng tiếp cận toàn cầu của chúng tôi đến gần 100 quốc gia có nghĩa là chúng tôi có sự hiện diện quốc tế để đáp ứng các yêu cầu về giá chuyển khoản của Quý Khách hàng.</span></li><li style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\">Các lợi ích phát sinh từ việc sử dụng hiệu quả giá chuyển khoản là gì?</span></li></ul><p style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\">Việc tận dụng các cơ hội được cung cấp bằng cách định giá chuyển khoản có thể cung cấp các lợi ích như:</span></p><p style=\"text-align:justify;\">&nbsp;</p><ul><li style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\">Thuế suất thấp hơn</span></li><li style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\">Cứu trợ cho tổn thất bị mắc kẹt</span></li><li style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\">Kiểm soát chi phí tốt hơn và</span></li><li style=\"text-align:justify;\"><span style=\"color:rgb(65,65,65);\">Cải thiện dòng tiền.</span></li></ul><p>&nbsp;</p><figure class=\"image\"><img src=\"data:image/png;base64,\" alt=\"Checklist\"></figure><h2><span style=\"color:rgb(255,192,0);\"><i><strong>&nbsp;Việc cần phải làm&nbsp;</strong></i></span><span style=\"color:windowtext;\"><i>của Quý Khách hàng<strong>:</strong></i></span></h2><ol><li style=\"text-align:justify;\">Lập hồ sơ và giải trình giao dịch liên kết theo giá thị trường theo quy định.</li><li style=\"text-align:justify;\">Rà soát và đánh giá rủi&nbsp;ro&nbsp;thông tin về giao dịch liên kết.</li><li style=\"text-align:justify;\">Bảo vệ hồ sơ giao dịch liên kết trước thanh tra thuế.</li><li style=\"text-align:justify;\">Đàm phán khi Doanh nghiệp bị kiểm tra về xác định giá thị trường.</li><li style=\"text-align:justify;\">Lập hồ sơ và giải trình thỏa thuận xác định giá trước.</li><li style=\"text-align:justify;\">Tuân thủ các thủ tục của cơ quan có thẩm quyền liên quan giao dịch liên kết.</li><li style=\"text-align:justify;\">Lập kế hoạch chuyển giá, rà soát đặc biệt và các thủ tục liên quan khác.</li></ol><p>&nbsp;</p><p>&nbsp;</p><h2><span style=\"color:rgb(68,114,196);\"><i><strong>MAN có khả năng!</strong></i></span></h2><p style=\"text-align:justify;\"><span style=\"color:rgb(68,114,196);\"><i><strong>Chúng tôi thực hiện những Giải pháp giá trị gì cho Quý Doanh nghiệp?</strong></i></span></p><figure class=\"image\"><img src=\"data:image/png;base64,\" alt=\"Medical\"></figure><h2><span style=\"color:rgb(255,192,0);\"><i><strong>&nbsp; Giảm thiểu Lo lắng, khó khăn, kết quả kém và rủi ro</strong></i></span><span style=\"color:windowtext;\"><i><strong>&nbsp;</strong>cho Quý Khách hàng<strong>:</strong></i></span></h2><ul><li>Chúng tôi giúp Quý Khách hàng kiểm tra, rà soát Quý Khách hàng có phải là đối tượng bắt buộc phải Lập báo cáo và hồ sơ giao dịch liên kết theo quy định không?!</li><li>Các kiểm toán viên của chúng tôi giúp đưa ra ý kiến độc lập về Lập báo cáo và hồ sơ giao dịch liên kết và các vấn đề liên quan đến thuế khác mà Quý Khách hàng và Cơ quan Thuế quan tâm.</li><li>Chúng tôi giúp Quý Khách hàng tuân thủ các quy định pháp luật của Nhà nước về Nghị định 20/2017/NĐ-CP và Thông tư 41/2017/TT-BTC và các vấn đề liên quan đến thuế khác nhằm giúp cho Quý Khách hàng có đầy đủ sự hiểu biết về giao dịch liên kết, đồng thời giúp Quý Khách hàng có cái nhìn tổng quan về việc vận dụng pháp luật về một cách phù hợp nhất.</li><li>Chúng tôi giúp Quý Doanh nghiệp có được Báo cáo quyết toán dự án hoàn thành đã được kiểm toán để nộp đúng thời gian theo quy định, không bị phạt vi phạm hành chính.</li><li>MAN là nhà cung cấp dịch vụ tư vấn chuyển giá hàng đầu tại Việt Nam xét về chất lượng, quy mô và danh tiếng trong ngành thuế.&nbsp;<a href=\"http://www.rsm.com.vn/dich-vu-chuyen-gia\"><span style=\"color:windowtext;\"><u>Dịch vụ chuyển giá&nbsp;</u></span></a>của MAN giúp Quý Doanh nghiệp nhận biết sớm các vấn đề tồn tại và việc tuân thủ các yêu cầu theo luật định rất quan trọng trong việc quản lý các rủi ro trong việc xác định giá thị trường.</li><li>MAN sẽ giúp Quý Khách hàng đáp ứng các yêu cầu khắt khe nhất về xác định giá thị trường, hỗ trợ lập hồ sơ báo cáo chống chuyển giá (Hồ sơ xác định giá giao dịch liên kết) và phụ lục, hỗ trợ phát triển chiến lược, lên kế hoạch việc xác định giá thị trường, hỗ trợ lập, xoát xét giải trình theo từng giao dịch theo giá thị trường và hỗ trợ đàm phán trong các trường hợp Quý Doanh nghiệp bị kiểm tra về xác định giá thị trường.</li></ul><figure class=\"image\"><img src=\"data:image/png;base64,\" alt=\"Thumbs Up Sign\"></figure><h2><span style=\"color:rgb(255,192,0);\"><i><strong>&nbsp; &nbsp;Những&nbsp;giải pháp giá trị cho</strong></i></span><span style=\"color:windowtext;\"><i> Quý Khách hàng<strong>:</strong></i></span></h2><p style=\"text-align:justify;\"><i><strong>Tại sao&nbsp;Quý khách hàng lựa chọn MAN tư vấn thực hiện dịch vụ chuyển giá?</strong></i></p><ul style=\"list-style-type:disc;\"><li>Đội ngũ&nbsp;Dịch vụ Chuyển giá của&nbsp;MAN giàu kinh nghiệm hợp tác với khách hàng trong suốt quá trình thực hiện chuyển giá. Chúng tôi tận dụng được kinh nghiệm tổng hợp của mạng lưới chuyên gia Chuyển giá từ nhiều nguồn lực khác nhau.</li><li>Chúng tôi hỗ trợ Quý Doanh nghiệp trong các giao dịch với các bên liên kết, với nguồn dữ liệu uy tín và được cơ quan thuế chấp thuận sẽ là cơ sở để MAN lựa chọn phương pháp xác định giá phù hợp và thuận lợi trong quá trình bảo vệ giá với cơ quan thuế khi thanh tra, kiểm tra tại Quý Doanh nghiệp.</li><li>Việc bố trí các chuyên gia chuyên trách về chuyển giá theo hướng tập trung vào từng ngành và phương pháp tiếp cận đa ngành cho phép chúng tôi có thể cung cấp cho khách hàng các chiến lược chuyển giá hiệu quả và bảo đảm tính tuân thủ.</li><li>Chúng tôi luôn luôn tập vào các&nbsp;nguyên tắc vững chắc cũng như bảo vệ được mọi quan điểm, số liệu của Quý Doanh nghiệp với Cơ quan Thuế. Bên cạnh đó, chúng tôi luôn xem xét các mục tiêu kinh doanh, yêu cầu hoạt động và thực tiễn hoạt động của khách hàng. Việc tạo ra hiệu quả về thuế và giảm nhẹ rủi ro về các rủi ro tiềm tàng xuất phát từ các cơ quan thuế luôn là nghiệm vụ trọng tâm của đội ngũ chúng tôi.</li></ul><figure class=\"image\"><img src=\"data:image/png;base64,\" alt=\"Handshake\"></figure><h2><span style=\"color:rgb(255,192,0);\"><i><strong>&nbsp; Dịch vụ</strong></i></span><strong>&nbsp;</strong><span style=\"color:rgb(255,192,0);\"><i><strong>tư vấn chuyển giá theo quy định&nbsp;</strong></i></span><span style=\"color:windowtext;\"><i>cho Quý Khách hàng<strong>:</strong></i></span></h2><ul><li>MAN là nhà cung cấp&nbsp;<a href=\"http://www.rsm.com.vn/dich-vu-chuyen-gia\"><span style=\"color:windowtext;\"><u>dịch vụ tư vấn chuyển giá&nbsp;</u></span></a>hàng đầu Việt Nam. Chất lượng, quy mô, uy tín và danh tiếng trong ngành thuế của MAN đã được khẳng định bởi khách hàng.&nbsp;<a href=\"http://www.rsm.com.vn/dich-vu-chuyen-gia\"><span style=\"color:windowtext;\"><u>Dịch vụ chuyển giá&nbsp;</u></span></a>của MAN giúp Quý Doanh nghiệp nhận biết sớm các vấn đề tồn tại và việc tuân thủ các yêu cầu theo luật định rất quan trọng trong việc quản lý các rủi ro trong việc xác định giá thị trường.</li><li>Các dịch vụ liên quan đến chuyển giá gồm:</li><li style=\"text-align:justify;\">Dịch vụ lập hồ sơ và giải trình giao dịch liên kết theo giá thị trường theo quy định.</li><li style=\"text-align:justify;\">Dịch vụ rà soát và đánh giá rủi&nbsp;ro&nbsp;thông tin về giao dịch liên kết.</li><li style=\"text-align:justify;\">Dich vụ bảo vệ hồ sơ giao dịch liên kết trước thanh tra thuế.</li><li style=\"text-align:justify;\">Dịch vụ đàm phán khi Doanh nghiệp bị kiểm tra về xác định giá thị trường.</li><li style=\"text-align:justify;\">Dịch vụ lập hồ sơ và giải trình thỏa thuận xác định giá trước.</li><li style=\"text-align:justify;\">Dịch vụ tuân thủ các thủ tục của cơ quan có thẩm quyền liên quan giao dịch liên kết.</li><li style=\"text-align:justify;\">Dịch vụ lập kế hoạch chuyển giá, rà soát đặc biệt và các thủ tục liên quan khác.</li><li>Tại MAN, chúng tôi cung cấp cho Quý Khách hàng dịch vụ chuyển giá. Đây là vấn đề thuế đặt biệt quan trọng đối với các Quý Doanh nghiệp đa quốc gia.</li><li>Dịch vụ của chúng tôi được công nhận rộng rãi và cung cấp cơ hội tiết kiệm thuế tuyệt vời nhưng nó cũng có thể làm phát sinh các vấn đề tuân thủ đáng kể. Vì những lý do này, các chính sách chuyển giá hiệu quả có tầm quan trọng cơ bản đối với các chiến lược quản lý rủi ro về thuế, đặc biệt trong môi trường kinh tế hiện tại.</li></ul><p>&nbsp;</p><p style=\"text-align:justify;\"><i><strong>MAN</strong></i><span style=\"color:black;\"><i><strong> khác biệt</strong></i></span></p><ul><li style=\"text-align:justify;\"><span style=\"color:black;\">Quá trình cung cấp dịch vụ của chúng tôi luôn gắn liền với việc hỗ trợ thông tin và tư vấn để hỗ trợ khách hàng:</span></li><li style=\"text-align:justify;\"><span style=\"color:black;\">Tư vấn thuế miễn phí cho Quý Khách hàng qua email, điện thoại,…</span></li><li style=\"text-align:justify;\"><span style=\"color:black;\">Tư vấn các vấn đề pháp lý kinh doanh.</span></li><li style=\"text-align:justify;\"><span style=\"color:black;\">Tư vấn công tác tổ chức kế toán, quản lý tài chính.</span></li><li style=\"text-align:justify;\"><span style=\"color:black;\">Tư vấn hệ&nbsp;thống kiểm soát nội bộ.</span></li><li style=\"text-align:justify;\"><span style=\"color:black;\">Tư vấn quyền và lợi ích chính đáng cho Quý Khách hàng.</span></li></ul><p style=\"text-align:justify;\">&nbsp;</p><p style=\"text-align:justify;\"><span style=\"color:black;\"><i><strong>“Làm đúng ngay từ đầu”</strong></i> là cách làm hiệu quả nhất, tốn ít chi phí nhất và khôn ngoan nhất. Hãy gọi ngay cho chúng tôi để “<i><strong>Làm đúng ngay từ đầu”</strong></i></span></p><p style=\"text-align:justify;\">&nbsp;</p><ul><li style=\"text-align:justify;\"><span style=\"color:black;\"><i>Quý Khách hàng đang cần trao đổi, tư vấn về chuyên môn? =&gt;&nbsp; <strong>Hãy liên hệ ngay:</strong></i></span></li></ul><figure class=\"image\"><img src=\"data:image/png;base64,\" alt=\"Envelope\"></figure><p style=\"text-align:justify;\">&nbsp; &nbsp; &nbsp;<a href=\"mailto:man@man.net.vn\"><u>man@man.net.vn</u></a></p><figure class=\"image\"><img src=\"data:image/png;base64,\" alt=\"Receiver\"></figure><p>&nbsp; &nbsp; &nbsp;(+84) 0903 963 163</p><p>Zalo&nbsp; (+84) 0903 963 163</p><p>&nbsp;</p><ul><li style=\"text-align:justify;\"><span style=\"color:black;\"><i>Quý Khách hàng đang cần báo giá? =&gt; <strong>Hãy nhận báo giá trong 1 giờ</strong></i></span></li></ul>', 1, 'admin', '2020-10-22 04:27:58', '2021-07-13 15:44:40', 'lehoangtuyen', 0, 4);
